{
  "name": "crucible",
  "version": "0.1.0",
  "private": true,
  "scripts": {
    "dev": "pnpm --filter desktop dev",
    "build": "pnpm run build:rust && pnpm run build:js",
    "build:rust": "cargo build --release",
    "build:js": "pnpm --filter '*' build",
    "test": "pnpm run test:rust && pnpm run test:js",
    "test:rust": "cargo test",
    "test:js": "pnpm --filter '*' test",
    "lint": "pnpm --filter '*' lint",
    "setup": "./scripts/setup.sh"
  },
  "devDependencies": {
    "@types/node": "^20.10.0",
    "typescript": "^5.3.0"
  },
  "engines": {
    "node": ">=20.0.0",
    "pnpm": ">=8.0.0"
  }
}
