/// Test suite for the symlink setup script
///
/// This test suite validates that the symlink creation logic works correctly
/// by testing the core functionality in isolation.

use std::fs;
use std::os::unix::fs::symlink;
use std::path::{Path, PathBuf};
use std::env;

/// Test structure for symlink validation
struct SymlinkTest {
    test_dir: PathBuf,
    temp_dirs: Vec<PathBuf>,
}

impl SymlinkTest {
    fn new() -> Self {
        let test_dir = env::temp_dir().join("symlink_test");
        fs::create_dir_all(&test_dir).unwrap();

        Self {
            test_dir,
